hudson.tasks.Shell.command:
#!/bin/bash
BASEPATH='/geppetto/generated-code/TestUpdate_1c449'
SYSTEMENTRYCODE='/geppetto/generated-code/TestUpdate_1c449/app/clients/desktop' SYSTEMENTRYIMAGENAME='tharanirajan/testupdate-1c449-app:1.0'
SYSTEMENTRYIMAGE=$(sudo docker images | awk '{ print $1,$2 }' | grep tharanirajan/testupdate-1c449-app | awk '{print $1 }')
echo "Runing....."
update_code () {
cd $BASEPATH
git pull if [ $? -eq 0 ]; then echo "Code updated sucessfully....." else echo "git pull failed!" fi
}
delete_if_existing_system_entry () {
if [ ! "$SYSTEMENTRYIMAGE" ]; then echo "testupdate-1c449-app:1.0 Image is not available" else echo "Deleting testupdate-1c449-app:1.0 Image" sudo docker rmi -f $SYSTEMENTRYIMAGENAME echo "Deleted...." fi
}
build_and_push_image_system_entry () {
cd $SYSTEMENTRYCODE npm install ng build if [ $? -eq 0 ]; then sudo docker build -t $SYSTEMENTRYIMAGENAME . if [ $? -eq 0 ]; then echo "image build sucessfully" sudo docker push $SYSTEMENTRYIMAGENAME if [ $? -eq 0 ]; then echo "image testupdate-1c449-app:1.0 pushed sucessfully" else echo "image testupdate-1c449-app:1.0 push failed" fi else echo "testupdate-1c449-app:1.0 image build failed" fi else echo "ng build failed!" fi }
#update_code delete_if_existing_system_entry build_and_push_image_system_entry
